						if (todo == 2) {//查询
							if(!("true").equals(isFirstChildWf)){
								columnnameAll = ftmc.getAssignColumnName();
							}else{
								columnnameAll = ftmc.getColumnname();
							}
						}else if (todo == 1) {//保存
//							if(!("true").equals(isFirstChildWf)){
//								columnnameAll = ftmc.getColumnname();
//							}else{
								columnnameAll = ftmc.getAssignColumnName();
//							}
						}
