	public String splitDepIdAndName(String oriStr, int index) {
		if (oriStr != null && oriStr.trim().length() > 0) {
			StringBuilder sb = new StringBuilder();
			String[] str = oriStr.split("[*]");
			sb.append(str[index]);
			sb.append(";");
			return sb.toString();
		}
		return "";
	}
	
	
