						String docNum = "";
						String orginDocNum = sw.getWh();
						if(CommonUtil.stringNotNULL(orginDocNum) && orginDocNum.contains("-")){
							orginDocNum = orginDocNum.substring(0, orginDocNum.indexOf("-"));
						}
						if(num > 0){
							docNum = orginDocNum + "-" + num;
						}
						tagValue += docNum;
